// N8N Custom Node - Processador de Imóveis
// Este código deve ser usado em um nó "Code" no n8n

// URL base para transformar URLs relativas em absolutas
const url_base = $('Config').first().json.url_stripe;

// Limite de caracteres para o campo description
const descriptionMaxLength = 300;

// Lista de campos configuráveis - customize conforme necessário
const fieldsToInclude = [
  'id',
  'title', 
  'description',
  'price',
  'tipo_contrato',
  'tipo_imovel',
  'bairro',
  'cidade',
  'tipologia'
];

// Configuração para seleção de imagem
const imageSelectionConfig = {
  // Opções: 'first', 'last', 'largest', 'smallest'
  strategy: 'first'
};

function processProperties(inputData) {
  if (!Array.isArray(inputData)) {
    throw new Error('Erro interno: processProperties esperava um array');
  }

  return inputData.map(property => {
    // Objeto de saída
    const processedProperty = {};

    // Adiciona campos configurados
    fieldsToInclude.forEach(field => {
      if (property.hasOwnProperty(field)) {
        let fieldValue = property[field];

        // Limita caracteres do campo description
        if (field === 'description' && typeof fieldValue === 'string') {
          fieldValue = limitDescription(fieldValue, descriptionMaxLength);
        }

        processedProperty[field] = fieldValue;
      }
    });

    // Processa imagens
    if (property.images && Array.isArray(property.images) && property.images.length > 0) {
      const selectedImage = selectImage(property.images, imageSelectionConfig);
      processedProperty.foto = selectedImage;
    } else {
      processedProperty.foto = null;
    }

    return processedProperty;
  });
}

function selectImage(images, config) {
  if (images.length === 0) return null;

  let selectedImage;

  // Seleciona imagem baseada na estratégia
  switch (config.strategy) {
    case 'first':
      selectedImage = images[0];
      break;
    case 'last':
      selectedImage = images[images.length - 1];
      break;
    case 'largest':
      selectedImage = images.reduce((largest, current) => 
        (current.size || 0) > (largest.size || 0) ? current : largest
      );
      break;
    case 'smallest':
      selectedImage = images.reduce((smallest, current) => 
        (current.size || Infinity) < (smallest.size || Infinity) ? current : smallest
      );
      break;
    default:
      selectedImage = images[0];
  }

  // Pega a URL original da imagem (diretamente do objeto, não de formats)
  // Esta é a URL que está em selectedImage.url, não em selectedImage.formats
  const imageUrl = makeAbsoluteUrl(selectedImage.url, url_base);

  // Retorna apenas a URL diretamente
  return imageUrl;
}

function limitDescription(description, maxLength) {
  if (!description || typeof description !== 'string') {
    return description;
  }

  if (description.length <= maxLength) {
    return description;
  }

  // Corta no limite e adiciona reticências
  let truncated = description.substring(0, maxLength);

  // Tenta cortar na última palavra completa para não quebrar no meio
  const lastSpaceIndex = truncated.lastIndexOf(' ');
  if (lastSpaceIndex > maxLength * 0.8) { // Se o último espaço não está muito longe do final
    truncated = truncated.substring(0, lastSpaceIndex);
  }

  return truncated.trim() + '...';
}

function makeAbsoluteUrl(url, baseUrl) {
  if (!url) return url;

  // Se já é uma URL absoluta, retorna como está
  if (url.startsWith('http://') || url.startsWith('https://')) {
    return url;
  }

  // Se é uma URL relativa, combina com a base
  if (url.startsWith('/')) {
    return baseUrl.replace(/\/$/, '') + url;
  }

  // Se não tem barra inicial, adiciona
  return baseUrl.replace(/\/$/, '') + '/' + url + '?tipo=image';
}

// Código principal para n8n
try {
  // No n8n, os dados de entrada estão em $input.all()
  const inputItems = $input.all();

  // Extrai o JSON dos itens de entrada
  let properties = inputItems[0].json;

  // Verifica se já é um array ou se precisa converter
  if (!Array.isArray(properties)) {
    // Se for um objeto único, transforma em array
    if (typeof properties === 'object' && properties !== null) {
      properties = [properties];
    } else {
      throw new Error('Dados de entrada inválidos. Esperado um objeto ou array de imóveis.');
    }
  }

  // Processa os imóveis
  const processedProperties = processProperties(properties);

  // Retorna os dados processados
  // No n8n, cada item do array se torna um item separado na saída
  return processedProperties.map(property => ({
    json: property
  }));

} catch (error) {
  // Em caso de erro, retorna informação do erro
  return [{
    json: {
      error: true,
      message: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

