// Defina a URL base
const base_url = $('Config').first().json.url_stripe;

// Função para processar o JSON
function transformPropertyData(inputData) {
  // Verificar se inputData existe e é um array
  if (!inputData || !Array.isArray(inputData)) {
    console.log('Input data is not a valid array:', inputData);
    return [];
  }

  // Verificar se o array não está vazio
  if (inputData.length === 0) {
    console.log('Input data is empty array');
    return [];
  }

  return inputData.map(property => {
    // Verificar se property existe e tem a propriedade images
    if (!property || !property.images || !Array.isArray(property.images)) {
      console.log('Property does not have valid images array:', property);
      return {
        ...property,
        images: []
      };
    }

    // Processar as imagens - usando URL original (não formats)
    const processedImages = property.images
      .map(image => {
        // Verificar se image existe e tem a URL original
        if (!image || !image.url) {
          return null;
        }

        // Usar a URL original da imagem (não de formats)
        return base_url + image.url + '?tipo=image';
      })
      .filter(url => url !== null); // Remover valores nulos

    // Retornar o objeto transformado
    return {
      ...property,
      images: processedImages
    };
  });
}

